-- List the names of all people who starred in a movie in which Kevin Bacon also starred
SELECT people.name FROM people JOIN stars ON people.id = stars.person_id JOIN movies ON movies.id = stars.movie_id WHERE movies.id IN (SELECT movies.id FROM people JOIN stars ON people.id = stars.person_id JOIN movies ON stars.movie_id = movies.id WHERE name = "Kevin Bacon" AND birth = 1958) AND people.name != "Kevin Bacon";